sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (newValue === '') {
		return;
	}

	var whatneededFieldname = 'u_what_needed';
    var whatneededOldValue = g_form.getValue(whatneededFieldname);

	g_form.clearOptions(whatneededFieldname);

	if (newValue == 'hr') {
		addWhatNeededHrOptions(whatneededFieldname);
	}

	if (newValue == 'facilities'){
		addWhatNeededFacilitiesOptions(whatneededFieldname);
	}

	if(newValue == 'legal') {
		addWhatNeededLegalsOptions(whatneededFieldname);
	}

	if (isNotNewRecord(isLoading)){
		g_form.setValue(whatneededFieldname, whatneededOldValue);
	}
}

function isNotNewRecord(isLoading) {
	return isLoading && !g_form.isNewRecord();
}

function addWhatNeededHrOptions(fieldname) {
	g_form.addOption(fieldname,'hr1','Human Resources 1');
	g_form.addOption(fieldname,'hr2','Human Resources 2');
	g_form.addOption(fieldname,'other','Other');
}

function addWhatNeededFacilitiesOptions(fieldname) {
	g_form.addOption(fieldname,'facilities1','Facilities 1');
	g_form.addOption(fieldname,'facilities2','Facilities 2');
	g_form.addOption(fieldname,'other','Other');
}

function addWhatNeededLegalsOptions(fieldname) {
	g_form.addOption(fieldname,'legal1','Legal 1');
	g_form.addOption(fieldname,'legal2','Legal 2');
	g_form.addOption(fieldname,'other','Other');
}
