sysauto_script.script:
function retrieveOverdueTasks(tableId) {
	var rightNow = new GlideDateTime();
	var closedCompleteState = 3;

	var overdueNITasks = new GlideRecord(tableId);
	overdueNITasks.addQuery('due_date','<=',rightNow);
	overdueNITasks.addQuery('state','!=', closedCompleteState);
	overdueNITasks.query();

	return overdueNITasks;
}

function logRecords(queryResults) {
	while(queryResults.next()) {
		gs.eventQueue('x_58872_needit.overdueNeedItTask', queryResults, queryResults.number, gs.getUserName());
		gs.info("Event: overdue NeedIt Task record: id = " + queryResults.number);
	}
}

var needitTasksTable = 'x_58872_needit_needit_task';
var overDueTasks = retrieveOverdueTasks(needitTasksTable);
logRecords(overDueTasks);
